import React, { useState } from "react";

function Chatbot({ devTools }) {
  const [userInput, setUserInput] = useState("");
  const [chatbotResponse, setChatbotResponse] = useState("");

  const handleUserInput = (e) => {
    setUserInput(e.target.value);
  }

  const handleSubmit = (e) => {
    e.preventDefault();

    // Match user input to dev tool
    const matchingDevTool = devTools.find(devTool => devTool.name.toLowerCase() === userInput.toLowerCase());
    if (matchingDevTool) {
      setChatbotResponse(`You might be interested in ${matchingDevTool.name}. It's a ${matchingDevTool.description}.`);
    } else {
      setChatbotResponse("Sorry, I couldn't find a matching dev tool for your needs. Please try again.");
    }
  }

  return (
    <div className="chatbot-container">
      <form onSubmit={handleSubmit}>
        <input 
          type="text" 
          placeholder="What are you looking for?"
          value={userInput}
          onChange={handleUserInput}
        />
        <button type="submit">Submit</button>
      </form>
      <p>{chatbotResponse}</p>
    </div>
  );
}

function MiraculousMarketplace() {
  const [devTools] = useState([
    {
      name: 'The Graph',
      logo: 'https://assets-global.website-files.com/6364e65656ab107e465325d2/637e69ad01e933f218693bf5_R4mv2md-MnCehqGhYgTFcu3TsE-lzwlBHhMnb7MUdQY.jpeg',
      description: "The Graph is a decentralized protocol for indexing and querying data from blockchains, starting with Ethereum. It makes it possible to query data that is difficult to query directly.",
      chains: ['Ethereum'],
      site: 'https://thegraph.com/en/',
      affiliateLink: 'https://thegraph.com/en/'
    },
    // Other dev tools
  ]);

  return (
    <div>
      <Chatbot devTools={devTools} />
      {/* Other components */}
    </div>
  );
}
